using System;using System.Collections;using System.ComponentModel;using System.Data;using System.Drawing;using System.Web;using System.Web.UI;using System.Web.UI.WebControls;using System.Web.UI.HtmlControls;using System.Data.SqlClient;
using Rainbow.Admin;using Rainbow.Configuration;
namespace Rainbow.DesktopModules{	/// <summary>	/// WeatherDEEdit Module - Editpage part	/// adapted from original version by: Mario Hartmann, Mario@Hartmann.net	///	/// Original WeatherUSEdit Module	/// Writen by: Jason Schaitel, Jason_Schaitel@hotmail.com	/// Moved into Rainbow by Jakob Hansen, hansen3000@hotmail.com	/// </summary>	public class WeatherDEEdit : Rainbow.UI.EditItemPage	{		protected System.Web.UI.WebControls.TextBox Textbox2;		protected System.Web.UI.WebControls.TextBox WeatherZip;		protected System.Web.UI.WebControls.RequiredFieldValidator rfvWeatherZip;		protected System.Web.UI.WebControls.TextBox WeatherCityIndex;		protected System.Web.UI.WebControls.RequiredFieldValidator rfvWeatherCityIndex;		protected System.Web.UI.WebControls.RadioButtonList WeatherSetting;		protected System.Web.UI.WebControls.RadioButtonList WeatherDesign;		protected System.Web.UI.WebControls.PlaceHolder PlaceHolderButtons;		protected System.Web.UI.WebControls.HyperLink HyperLink;					private void Page_Load(object sender, System.EventArgs e)		{			// Construct the page			// Added css Styles by Mario Endara <mario@softworks.com.uy> (2004/10/26)			updateButton.CssClass = "CommandButton";			PlaceHolderButtons.Controls.Add(updateButton);			PlaceHolderButtons.Controls.Add(new LiteralControl("&#160;"));			cancelButton.CssClass = "CommandButton";			PlaceHolderButtons.Controls.Add(cancelButton);			if (Page.IsPostBack == false) 			{				WeatherZip.Text = "88045";				WeatherCityIndex.Text = "0";				WeatherSetting.SelectedIndex = 0;				if (ModuleID > 0) 				{					if  (moduleSettings["WeatherZip"] != null)						WeatherZip.Text = ((SettingItem) moduleSettings["WeatherZip"]).ToString();										if  (moduleSettings["WeatherCityIndex"] != null)						WeatherCityIndex.Text = ((SettingItem) moduleSettings["WeatherCityIndex"]).ToString();										if  (moduleSettings["WeatherSetting"] != null)						WeatherSetting.SelectedIndex = int.Parse(((SettingItem) moduleSettings["WeatherSetting"]).ToString());										if  (moduleSettings["WeatherDesign"] != null)					{						for (int i = 0; i < WeatherDesign.Items.Count ; i++)							if (WeatherDesign.Items[i].Value ==(((SettingItem) moduleSettings["WeatherDesign"]).ToString())) WeatherDesign.SelectedIndex = i ;					}				}			}		}		/// <summary>		/// Set the module guids with free access to this page		/// </summary>		protected override ArrayList AllowedModules		{			get			{				ArrayList al = new ArrayList();				al.Add ("D3182CD6-DAFF-4E72-AD9E-0B28CB44F000");				return al;			}		}		/// <summary>		/// TBD		/// </summary>		/// <param name="e"></param>		override protected void OnUpdate(EventArgs e)		{			base.OnUpdate(e);			//only Update if the entered data is Valid			if (Page.IsValid == true)			{				// Update settings in the database				ModuleSettings.UpdateModuleSetting(ModuleID, "WeatherZip", WeatherZip.Text);				ModuleSettings.UpdateModuleSetting(ModuleID, "WeatherCityIndex", WeatherCityIndex.Text);				ModuleSettings.UpdateModuleSetting(ModuleID, "WeatherSetting", WeatherSetting.Items[WeatherSetting.SelectedIndex].Value) ;				ModuleSettings.UpdateModuleSetting(ModuleID, "WeatherDesign", WeatherDesign.Items[WeatherDesign.SelectedIndex].Value);				this.RedirectBackToReferringPage();			}		}						#region Web Form Designer generated code
		/// <summary>		/// Raises OnInitEvent		/// </summary>		/// <param name="e"></param>		protected override void OnInit(EventArgs e)		{			//Controls must be created here			updateButton = new LinkButton();			cancelButton = new LinkButton();			InitializeComponent();			base.OnInit(e);		}		/// <summary>		/// Required method for Designer support - do not modify		/// the contents of this method with the code editor.		/// </summary>		private void InitializeComponent() 		{    			this.Load += new System.EventHandler(this.Page_Load);		}		#endregion
	}
}